				Name:   "",
				Type:   "standard",
				Branch: "feat/test",
				Statechart: project.StatechartState{
					Current_state: "Active",
					Updated_at:    now,
				},
				Created_at: now,
				Updated_at: now,
			},
		},
		{
			name: "empty type",
			state: &project.ProjectState{
				Name:   "valid-name",
				Type:   "",
				Branch: "feat/test",
				Statechart: project.StatechartState{
					Current_state: "Active",
					Updated_at:    now,
				},
				Created_at: now,
				Updated_at: now,
			},
		},
		{
			name: "empty branch",
			state: &project.ProjectState{
				Name:   "valid-name",
				Type:   "standard",
				Branch: "",
				Statechart: project.StatechartState{
					Current_state: "Active",
					Updated_at:    now,
				},
				Created_at: now,
				Updated_at: now,
			},
		},
		{
			name: "empty current_state",
			state: &project.ProjectState{
				Name:   "valid-name",
				Type:   "standard",
				Branch: "feat/test",
				Statechart: project.StatechartState{
					Current_state: "",
					Updated_at:    now,
				},
				Created_at: now,
				Updated_at: now,
