	phase, exists := p.Phases[phaseName]
	if !exists {
		return fmt.Errorf("phase %s not found", phaseName)
	}

	// Get current iteration (0 if not set)
	currentIter := phase.Iteration

	// Increment
	phase.Iteration = currentIter + 1
	p.Phases[phaseName] = phase
	return nil
}

