    11→// GitHubCLI implements GitHubClient using the gh CLI tool.
    12→//
    13→// All operations require the GitHub CLI (gh) to be installed and authenticated.
    14→// The client accepts an Executor interface, making it easy to mock in tests.
    15→//
    16→// For auto-detection between CLI and API clients, use NewGitHubClient() factory.
    17→type GitHubCLI struct {
    18→	gh exec.Executor
    19→}
    20→
    21→// NewGitHubCLI creates a new GitHub CLI client with the given executor.
    22→//
    23→// The executor should be configured for the "gh" command. For production use:
    24→//
    25→//	ghExec := exec.NewLocal("gh")
    26→//	github := sow.NewGitHubCLI(ghExec)
    27→//
    28→// For testing with a mock:
    29→//
    30→//	mockExec := &MockExecutor{...}
    31→//	github := sow.NewGitHubCLI(mockExec)
    32→//
    33→// Note: This does NOT check if gh is installed or authenticated.
    34→// Those checks happen on first operation via Ensure().
    35→func NewGitHubCLI(executor exec.Executor) *GitHubCLI {
    36→	return &GitHubCLI{
    37→		gh: executor,
    38→	}
    39→}
    40→
    41→// NewGitHub creates a GitHub CLI client.
    42→// Deprecated: Use NewGitHubCLI() for explicit CLI client, or NewGitHubClient() for auto-detection.
    43→func NewGitHub(executor exec.Executor) *GitHubCLI {
    44→	return NewGitHubCLI(executor)
    45→}
    46→
    47→// Error types for GitHub operations
    48→
    49→// ErrGHNotInstalled is returned when the gh CLI is not found.
    50→type ErrGHNotInstalled struct{}
    51→
    52→func (e ErrGHNotInstalled) Error() string {
    53→	return "GitHub CLI (gh) not found. Install from: https://cli.github.com/"
    54→}
    55→
    56→// ErrGHNotAuthenticated is returned when gh is not authenticated.
    57→type ErrGHNotAuthenticated struct{}
    58→
    59→func (e ErrGHNotAuthenticated) Error() string {
    60→	return "GitHub CLI not authenticated. Run: gh auth login"
    61→}
    62→
    63→// ErrGHCommand is returned when a gh command fails.
    64→type ErrGHCommand struct {
    65→	Command string
    66→	Stderr  string
    67→	Err     error
    68→}
    69→
    70→func (e ErrGHCommand) Error() string {
    71→	if e.Stderr != "" {
    72→		return fmt.Sprintf("gh %s failed: %s", e.Command, e.Stderr)
    73→	}
    74→	return fmt.Sprintf("gh %s failed: %v", e.Command, e.Err)
    75→}
    76→
    77→func (e ErrGHCommand) Unwrap() error {
    78→	return e.Err
    79→}
    80→
    81→// Data types
    82→
    83→// Issue represents a GitHub issue.
    84→type Issue struct {
    85→	Number int    `json:"number"`
    86→	Title  string `json:"title"`
    87→	Body   string `json:"body"`
    88→	State  string `json:"state"`
    89→	URL    string `json:"url"`
    90→	Labels []struct {
    91→		Name string `json:"name"`
    92→	} `json:"labels"`
    93→}
    94→
    95→// LinkedBranch represents a branch linked to an issue.
    96→type LinkedBranch struct {
    97→	Name string
    98→	URL  string
    99→}
   100→
   101→// HasLabel checks if an issue has a specific label.
   102→func (i *Issue) HasLabel(label string) bool {
   103→	for _, l := range i.Labels {
   104→		if l.Name == label {
   105→			return true
   106→		}
   107→	}
   108→	return false
   109→}
   110→
   111→// Installation and authentication checks
   112→
   113→// CheckInstalled verifies that the gh CLI is installed and available.
   114→func (g *GitHubCLI) CheckInstalled() error {
   115→	if !g.gh.Exists() {
   116→		return ErrGHNotInstalled{}
   117→	}
   118→	return nil
   119→}
   120→
   121→// CheckAuthenticated verifies that the gh CLI is authenticated.
   122→func (g *GitHubCLI) CheckAuthenticated() error {
   123→	// gh auth status exits with code 1 if not authenticated
   124→	// but writes to stderr in both success and failure cases
   125→	if err := g.gh.RunSilent("auth", "status"); err != nil {
   126→		return ErrGHNotAuthenticated{}
   127→	}
   128→
   129→	return nil
   130→}
   131→
   132→// Ensure checks that gh is installed and authenticated.
   133→//
   134→// This should be called before any GitHub operation to provide
   135→// clear error messages to the user.
   136→func (g *GitHubCLI) Ensure() error {
   137→	if err := g.CheckInstalled(); err != nil {
   138→		return err
   139→	}
   140→	if err := g.CheckAuthenticated(); err != nil {
   141→		return err
   142→	}
   143→	return nil
   144→}
   145→
   146→// CheckAvailability implements GitHubClient.
   147→// For CLI client, this checks that gh is installed and authenticated.
   148→func (g *GitHubCLI) CheckAvailability() error {
   149→	return g.Ensure()
   150→}
   151→
   152→// Issue operations
   153→
   154→// ListIssues lists issues with the given label and state.
   155→//
   156→// Parameters:
   157→//   - label: Filter issues by label (e.g., "sow")
   158→//   - state: Filter by state ("open", "closed", or "all")
   159→//
   160→// Returns up to 1000 issues matching the criteria.
   161→func (g *GitHubCLI) ListIssues(label, state string) ([]Issue, error) {
   162→	if err := g.Ensure(); err != nil {
   163→		return nil, err
   164→	}
   165→
   166→	stdout, stderr, err := g.gh.Run(
   167→		"issue", "list",
   168→		"--label", label,
   169→		"--state", state,
   170→		"--json", "number,title,url,state,labels",
   171→		"--limit", "1000",
   172→	)
   173→	if err != nil {
   174→		return nil, ErrGHCommand{
   175→			Command: fmt.Sprintf("issue list --label %s --state %s", label, state),
   176→			Stderr:  stderr,
   177→			Err:     err,
   178→		}
   179→	}
   180→
   181→	var issues []Issue
   182→	if err := json.Unmarshal([]byte(stdout), &issues); err != nil {
   183→		return nil, fmt.Errorf("failed to parse issue list: %w", err)
   184→	}
   185→
   186→	return issues, nil
   187→}
   188→
   189→// GetIssue retrieves a single issue by number.
   190→func (g *GitHubCLI) GetIssue(number int) (*Issue, error) {
   191→	if err := g.Ensure(); err != nil {
   192→		return nil, err
   193→	}
   194→
   195→	stdout, stderr, err := g.gh.Run(
   196→		"issue", "view", fmt.Sprintf("%d", number),
   197→		"--json", "number,title,body,url,state,labels",
   198→	)
   199→	if err != nil {
   200→		return nil, ErrGHCommand{
   201→			Command: fmt.Sprintf("issue view %d", number),
   202→			Stderr:  stderr,
   203→			Err:     err,
   204→		}
   205→	}
   206→
   207→	var issue Issue
   208→	if err := json.Unmarshal([]byte(stdout), &issue); err != nil {
   209→		return nil, fmt.Errorf("failed to parse issue: %w", err)
   210→	}
   211→
   212→	return &issue, nil
   213→}
   214→
   215→// GetLinkedBranches returns branches linked to an issue.
   216→//
   217→// Returns an empty slice if no branches are linked (not an error).
   218→func (g *GitHubCLI) GetLinkedBranches(number int) ([]LinkedBranch, error) {
   219→	if err := g.Ensure(); err != nil {
   220→		return nil, err
   221→	}
   222→
   223→	stdout, stderr, err := g.gh.Run(
   224→		"issue", "develop", "--list", fmt.Sprintf("%d", number),
   225→	)
   226→	if err != nil {
   227→		// Check if error is because no branches are linked
   228→		if strings.Contains(stderr, "no linked branches") ||
   229→			strings.Contains(stderr, "No linked branches") {
   230→			return []LinkedBranch{}, nil
   231→		}
   232→
   233→		return nil, ErrGHCommand{
   234→			Command: fmt.Sprintf("issue develop --list %d", number),
   235→			Stderr:  stderr,
   236→			Err:     err,
   237→		}
   238→	}
   239→
   240→	// Parse output - gh issue develop --list returns tab-separated values
   241→	// Format: BRANCH    URL
   242→	lines := strings.Split(strings.TrimSpace(stdout), "\n")
   243→
   244→	var branches []LinkedBranch
   245→	for i, line := range lines {
   246→		// Skip header line
   247→		if i == 0 {
   248→			continue
   249→		}
   250→
   251→		// Skip empty lines
   252→		line = strings.TrimSpace(line)
   253→		if line == "" {
   254→			continue
   255→		}
   256→
   257→		// Split by whitespace (tabs or spaces)
   258→		parts := strings.Fields(line)
   259→		if len(parts) >= 2 {
   260→			branches = append(branches, LinkedBranch{
   261→				Name: parts[0],
   262→				URL:  parts[1],
   263→			})
   264→		}
   265→	}
   266→
   267→	return branches, nil
   268→}
   269→
   270→// CreateLinkedBranch creates a branch linked to an issue using gh issue develop.
   271→//
   272→// Parameters:
   273→//   - issueNumber: The issue number to link to
   274→//   - branchName: Custom branch name (empty string for auto-generated)
   275→//   - checkout: Whether to checkout the branch after creation
   276→//
   277→// Returns the branch name that was created.
   278→func (g *GitHubCLI) CreateLinkedBranch(issueNumber int, branchName string, checkout bool) (string, error) {
   279→	if err := g.Ensure(); err != nil {
   280→		return "", err
   281→	}
   282→
   283→	args := []string{
   284→		"issue", "develop", fmt.Sprintf("%d", issueNumber),
   285→	}
   286→
   287→	if branchName != "" {
   288→		args = append(args, "--name", branchName)
   289→	}
   290→
   291→	if checkout {
   292→		args = append(args, "--checkout")
   293→	}
   294→
   295→	stdout, stderr, err := g.gh.Run(args...)
   296→	if err != nil {
   297→		return "", ErrGHCommand{
   298→			Command: fmt.Sprintf("issue develop %d", issueNumber),
   299→			Stderr:  stderr,
   300→			Err:     err,
   301→		}
   302→	}
   303→
   304→	// Parse output to get branch name
   305→	// gh issue develop outputs: "Created branch <name> based on <base>"
   306→	// or: "Checked out branch <name> for issue #<number>"
   307→	output := stdout
   308→
   309→	// If we specified a name, use that
   310→	if branchName != "" {
   311→		return branchName, nil
   312→	}
   313→
   314→	// Otherwise, parse from output
   315→	// Look for "Created branch" or "Checked out branch"
   316→	lines := strings.Split(output, "\n")
   317→	for _, line := range lines {
   318→		if strings.Contains(line, "Created branch") || strings.Contains(line, "Checked out branch") {
   319→			parts := strings.Fields(line)
   320→			if len(parts) >= 3 {
   321→				return parts[2], nil
   322→			}
   323→		}
   324→	}
   325→
   326→	// If we can't parse the output, construct the expected branch name
   327→	// gh creates branches in the format: <issue-number>-<title-kebab-case>
   328→	// We'll need to query the issue to get the title
   329→	issue, err := g.GetIssue(issueNumber)
   330→	if err != nil {
   331→		return "", fmt.Errorf("failed to get issue for branch name: %w", err)
   332→	}
   333→
   334→	// Convert title to kebab-case
   335→	branchName = fmt.Sprintf("%d-%s", issueNumber, toKebabCase(issue.Title))
   336→	return branchName, nil
   337→}
   338→
   339→// Pull request operations
   340→
   341→// CreateIssue creates a GitHub issue using gh CLI.
   342→//
   343→// Parameters:
   344→//   - title: Issue title
   345→//   - body: Issue body/description (supports markdown)
   346→//   - labels: List of labels to add to the issue
   347→//
   348→// Returns the created Issue on success.
   349→func (g *GitHubCLI) CreateIssue(title, body string, labels []string) (*Issue, error) {
   350→	if err := g.Ensure(); err != nil {
   351→		return nil, err
   352→	}
   353→
   354→	// Build command arguments
   355→	args := []string{"issue", "create", "--title", title, "--body", body}
   356→
   357→	// Add labels if provided
   358→	for _, label := range labels {
   359→		args = append(args, "--label", label)
   360→	}
   361→
   362→	stdout, stderr, err := g.gh.Run(args...)
   363→	if err != nil {
   364→		return nil, ErrGHCommand{
   365→			Command: "issue create",
   366→			Stderr:  stderr,
   367→			Err:     err,
   368→		}
   369→	}
   370→
   371→	// gh issue create outputs the issue URL on the last line
   372→	output := strings.TrimSpace(stdout)
   373→	lines := strings.Split(output, "\n")
   374→	if len(lines) == 0 {
   375→		return nil, fmt.Errorf("unexpected empty output from gh issue create")
   376→	}
   377→
   378→	// The URL is typically the last line
   379→	issueURL := strings.TrimSpace(lines[len(lines)-1])
   380→
   381→	// Validate it looks like a URL
   382→	if !strings.HasPrefix(issueURL, "http") {
   383→		return nil, fmt.Errorf("unexpected issue create output (no URL found): %s", output)
   384→	}
   385→
   386→	// Parse issue number from URL (format: https://github.com/owner/repo/issues/NUMBER)
   387→	parts := strings.Split(issueURL, "/")
   388→	if len(parts) < 1 {
   389→		return nil, fmt.Errorf("could not parse issue number from URL: %s", issueURL)
   390→	}
   391→	issueNumberStr := parts[len(parts)-1]
   392→	issueNumber := 0
   393→	_, err = fmt.Sscanf(issueNumberStr, "%d", &issueNumber)
   394→	if err != nil {
   395→		return nil, fmt.Errorf("could not parse issue number from URL: %s", issueURL)
   396→	}
   397→
   398→	return &Issue{
   399→		Number: issueNumber,
   400→		Title:  title,
   401→		Body:   body,
   402→		URL:    issueURL,
   403→		State:  "open",
   404→	}, nil
   405→}
   406→
   407→// CreatePullRequest creates a pull request using gh CLI, optionally as a draft.
   408→//
   409→// The PR is created from the current branch to the repository's default base branch.
   410→//
   411→// Parameters:
   412→//   - title: PR title (required, non-empty)
   413→//   - body: PR description (supports markdown)
   414→//   - draft: If true, creates the PR as a draft; if false, creates as ready for review
   415→//
   416→// Returns:
   417→//   - number: The PR number, which can be used with UpdatePullRequest and MarkPullRequestReady
   418→//   - url: Full GitHub URL to the pull request (e.g., "https://github.com/owner/repo/pull/42")
   419→//   - error: Any error during PR creation
   420→//
   421→// Draft PRs can be converted to ready for review using MarkPullRequestReady.
   422→func (g *GitHubCLI) CreatePullRequest(title, body string, draft bool) (int, string, error) {
   423→	if err := g.Ensure(); err != nil {
   424→		return 0, "", err
   425→	}
   426→
   427→	// Build command arguments
   428→	args := []string{"pr", "create", "--title", title, "--body", body}
   429→	if draft {
   430→		args = append(args, "--draft")
   431→	}
   432→
   433→	stdout, stderr, err := g.gh.Run(args...)
   434→	if err != nil {
   435→		return 0, "", ErrGHCommand{
   436→			Command: "pr create",
   437→			Stderr:  stderr,
   438→			Err:     err,
   439→		}
   440→	}
   441→
   442→	// gh pr create outputs the PR URL on the last line
   443→	output := strings.TrimSpace(stdout)
   444→	lines := strings.Split(output, "\n")
   445→	if len(lines) == 0 {
   446→		return 0, "", fmt.Errorf("unexpected empty output from gh pr create")
   447→	}
   448→
   449→	// The URL is typically the last line
   450→	prURL := strings.TrimSpace(lines[len(lines)-1])
   451→
   452→	// Validate it looks like a URL
   453→	if !strings.HasPrefix(prURL, "http") {
   454→		return 0, "", fmt.Errorf("unexpected pr create output (no URL found): %s", output)
   455→	}
   456→
   457→	// Parse PR number from URL (format: https://github.com/owner/repo/pull/NUMBER)
   458→	parts := strings.Split(prURL, "/")
   459→	if len(parts) < 1 {
   460→		return 0, "", fmt.Errorf("could not parse PR number from URL: %s", prURL)
   461→	}
   462→	prNumberStr := parts[len(parts)-1]
   463→	prNumber := 0
   464→	_, err = fmt.Sscanf(prNumberStr, "%d", &prNumber)
   465→	if err != nil {
   466→		return 0, "", fmt.Errorf("could not parse PR number from URL: %s", prURL)
   467→	}
   468→
   469→	return prNumber, prURL, nil
   470→}
   471→
   472→// UpdatePullRequest updates an existing pull request's title and body.
   473→//
   474→// This is useful for updating PR descriptions as implementation progresses, or for
   475→// correcting the title after creation.
   476→//
   477→// Parameters:
   478→//   - number: PR number to update
   479→//   - title: New PR title (required, non-empty)
   480→//   - body: New PR description (supports markdown)
   481→func (g *GitHubCLI) UpdatePullRequest(number int, title, body string) error {
   482→	if err := g.Ensure(); err != nil {
   483→		return err
   484→	}
   485→
   486→	stdout, stderr, err := g.gh.Run(
   487→		"pr", "edit", fmt.Sprintf("%d", number),
   488→		"--title", title,
   489→		"--body", body,
   490→	)
   491→	_ = stdout // stdout not used but kept for consistency
   492→
   493→	if err != nil {
   494→		return ErrGHCommand{
   495→			Command: fmt.Sprintf("pr edit %d", number),
   496→			Stderr:  stderr,
   497→			Err:     err,
   498→		}
   499→	}
   500→
   501→	return nil
   502→}
   503→
   504→// MarkPullRequestReady converts a draft pull request to "ready for review" state.
   505→//
   506→// This operation is only valid for PRs that were created as drafts. Calling this
   507→// on a PR that is already ready for review may return an error.
   508→//
   509→// Parameters:
   510→//   - number: Draft PR number to mark as ready
   511→func (g *GitHubCLI) MarkPullRequestReady(number int) error {
   512→	if err := g.Ensure(); err != nil {
   513→		return err
   514→	}
   515→
   516→	stdout, stderr, err := g.gh.Run(
   517→		"pr", "ready", fmt.Sprintf("%d", number),
   518→	)
   519→	_ = stdout // stdout not used but kept for consistency
   520→
   521→	if err != nil {
   522→		return ErrGHCommand{
   523→			Command: fmt.Sprintf("pr ready %d", number),
   524→			Stderr:  stderr,
   525→			Err:     err,
   526→		}
   527→	}
   528→
   529→	return nil
   530→}
   531→
   532→// Helper functions
   533→
   534→// toKebabCase converts a string to kebab-case.
   535→func toKebabCase(s string) string {
   536→	// Convert to lowercase
   537→	s = strings.ToLower(s)
   538→
   539→	// Replace spaces and underscores with hyphens
   540→	s = strings.ReplaceAll(s, " ", "-")
   541→	s = strings.ReplaceAll(s, "_", "-")
   542→
   543→	// Remove any non-alphanumeric characters except hyphens
   544→	var result strings.Builder
   545→	for _, r := range s {
   546→		if (r >= 'a' && r <= 'z') || (r >= '0' && r <= '9') || r == '-' {
   547→			result.WriteRune(r)
   548→		}
   549→	}
   550→
   551→	// Remove consecutive hyphens
