   205→// GetAvailableTransitions returns all configured transitions from a state.
   206→// This returns the transitions defined in the project type configuration,
   207→// not filtered by guards.
   208→func (ptc *ProjectTypeConfig) GetAvailableTransitions(from State) []TransitionInfo {
   209→	var result []TransitionInfo
   210→
   211→	// Check if this is a branching state
   212→	branchConfig, isBranching := ptc.branches[from]
   213→	if isBranching {
   214→		// Add transitions from branch paths
   215→		for _, path := range branchConfig.branches {
   216→			result = append(result, TransitionInfo{
   217→				Event:       path.event,
   218→				From:        from,
   219→				To:          path.to,
   220→				Description: path.description,
   221→				GuardDesc:   path.guardTemplate.Description,
   222→			})
   223→		}
   224→	}
   225→
   226→	// Add direct transitions (from AddTransition calls)
   227→	for _, tc := range ptc.transitions {
   228→		if tc.From != from {
   229→			continue
   230→		}
   231→		// Skip if this transition is part of a branch (already included above)
   232→		if isBranching && ptc.isBranchTransition(branchConfig, tc) {
   233→			continue
   234→		}
   235→		result = append(result, TransitionInfo{
   236→			Event:       tc.Event,
   237→			From:        tc.From,
   238→			To:          tc.To,
   239→			Description: tc.description,
   240→			GuardDesc:   tc.guardTemplate.Description,
   241→		})
   242→	}
   243→
   244→	// Sort by event name for deterministic output
   245→	sort.Slice(result, func(i, j int) bool {
   246→		return result[i].Event < result[j].Event
   247→	})
   248→
   249→	return result
   250→}
   251→
   252→// isBranchTransition checks if a transition is part of a branch configuration.
   253→func (ptc *ProjectTypeConfig) isBranchTransition(bc *BranchConfig, tc TransitionConfig) bool {
   254→	for _, path := range bc.branches {
   255→		if tc.Event == path.event && tc.To == path.to {
   256→			return true
   257→		}
   258→	}
   259→	return false
   260→}
   261→
   262→// TransitionInfo describes a single available transition from a state.
   263→// Used by introspection methods to provide structured information about
   264→// state machine configuration without exposing internal implementation details.
   265→type TransitionInfo struct {
   266→	Event       Event  // Event that triggers this transition
   267→	From        State  // Source state
   268→	To          State  // Target state
   269→	Description string // Human-readable description (empty if not provided)
   270→	GuardDesc   string // Guard description (empty if no guard)
   271→}
   272→
   273→// BuildProjectMachine builds a state machine for a project using this project type's configuration.
   274→// It creates a state machine with all configured transitions, binding guard templates and
   275→// actions to the project instance via closures.
   276→//
   277→// Guard templates (func(*Project) bool) are bound to guard functions (func() bool) by
   278→// capturing the project in a closure. This allows guards to access live project state
   279→// while matching the state machine SDK's expected signature.
   280→//
   281→// Similarly, onEntry and onExit actions are bound via closures to match the SDK's
   282→// expected signature (func(context.Context, ...any) error).
   283→//
   284→// Parameters:
   285→//   - project: The project instance to bind to guards and actions
   286→//   - initialState: The starting state for the machine
   287→//
   288→// Returns:
   289→//   - A configured state machine ready to handle events
   290→//
   291→// Example usage:
   292→//
   293→//	config := NewProjectTypeConfigBuilder("standard").
   294→//	    AddTransition(
   295→//	        StateActive,
   296→//	        StateComplete,
   297→//	        EventFinish,
   298→//	        WithProjectGuard("all tasks complete", func(p *state.Project) bool {
   299→//	            return p.AllTasksComplete()
   300→//	        }),
   301→//	    ).
   302→//	    Build()
   303→//
   304→//	machine := config.BuildProjectMachine(project, StateActive)
   305→func (ptc *ProjectTypeConfig) BuildProjectMachine(
   306→	proj *state.Project,
   307→	initialState State,
   308→) *Machine {
   309→	// Create prompt function that uses project type config prompts
   310→	var promptFunc PromptFunc
   311→	if len(ptc.prompts) > 0 {
   312→		promptFunc = func(s State) string {
   313→			gen := ptc.prompts[s]
   314→			if gen == nil {
   315→				return "" // No prompt configured for this state
   316→			}
   317→			return gen(proj) // Call project type config prompt generator
   318→		}
   319→	}
   320→
   321→	builder := NewBuilder(initialState, promptFunc)
   322→
   323→	// Add all transitions with guards and actions bound to project instance
   324→	for _, tc := range ptc.transitions {
   325→		var opts []TransitionOption
   326→
   327→		// Bind guard template to project instance via closure
   328→		// Need to capture tc.guardTemplate in a local variable for the closure
   329→		if tc.guardTemplate.Func != nil {
   330→			guardFunc := tc.guardTemplate.Func
   331→			if tc.guardTemplate.Description != "" {
   332→				guardDesc := tc.guardTemplate.Description
   333→				// Use WithGuardDescription if description is provided
   334→				opts = append(opts, WithGuardDescription(guardDesc, func() bool {
   335→					return guardFunc(proj)
   336→				}))
   337→			} else {
   338→				// Fallback to WithGuard without description
   339→				opts = append(opts, WithGuard(func() bool {
   340→					return guardFunc(proj)
   341→				}))
   342→			}
   343→		}
   344→
   345→		// Bind onExit action to project instance via closure
   346→		if tc.onExit != nil {
   347→			onExitFunc := tc.onExit // Capture for closure
   348→			opts = append(opts, WithOnExit(func(_ context.Context, _ ...any) error {
   349→				return onExitFunc(proj)
   350→			}))
   351→		}
   352→
   353→		// Bind onEntry action to project instance via closure
   354→		if tc.onEntry != nil {
   355→			onEntryFunc := tc.onEntry // Capture for closure
   356→			opts = append(opts, WithOnEntry(func(_ context.Context, _ ...any) error {
   357→				return onEntryFunc(proj)
   358→			}))
   359→		}
   360→
   361→		builder.AddTransition(
   362→			tc.From,
   363→			tc.To,
   364→			tc.Event,
   365→			opts...,
   366→		)
   367→	}
   368→
   369→	return builder.Build()
   370→}
   371→
   372→// FireWithPhaseUpdates fires an event and automatically updates phase statuses
   373→// based on the state transition. This wraps the standard Fire() call with
   374→// automatic phase status management.
   375→//
   376→// Phase status updates:
   377→//   - When exiting a phase's end state -> mark phase "completed" (unless WithProjectFailedPhase specified)
   378→//   - When entering a phase's start state -> mark phase "in_progress" (only if "pending")
   379→//
   380→// This approach respects explicit status changes (like MarkPhaseFailed) while
   381→// automating the common case of successful phase progression.
   382→//
   383→// Example usage:
   384→//
   385→//	err := config.FireWithPhaseUpdates(machine, EventPlanningComplete, project)
   386→func (ptc *ProjectTypeConfig) FireWithPhaseUpdates(
   387→	machine *Machine,
   388→	event Event,
   389→	proj *state.Project,
   390→) error {
   391→	// Capture old state before transition
   392→	oldState := machine.State()
   393→
   394→	// Fire the event (executes user-defined guards and actions)
   395→	if err := machine.Fire(event); err != nil {
   396→		return &ErrTransitionFailed{Cause: err}
   397→	}
   398→
   399→	// Capture new state after transition
   400→	newState := machine.State()
   401→
   402→	// Look up the transition config to check for special phase status handling
   403→	transitionConfig := ptc.GetTransition(oldState, newState, event)
   404→
   405→	// Update phase status when exiting a phase's end state
   406→	if err := ptc.updateExitingPhaseStatus(oldState, transitionConfig, proj); err != nil {
   407→		return err
   408→	}
   409→
   410→	// Update phase status when entering a phase's start state
   411→	if err := ptc.updateEnteringPhaseStatus(newState, proj); err != nil {
   412→		return err
   413→	}
   414→
   415→	return nil
   416→}
   417→
   418→// updateExitingPhaseStatus updates the status of a phase when exiting its end state.
   419→// Marks the phase as "failed" if configured in the transition, otherwise "completed".
   420→func (ptc *ProjectTypeConfig) updateExitingPhaseStatus(
   421→	oldState State,
   422→	transitionConfig *TransitionConfig,
   423→	proj *state.Project,
   424→) error {
   425→	phaseName := ptc.GetPhaseForState(oldState)
   426→	if phaseName == "" {
   427→		return nil
   428→	}
   429→
   430→	if !ptc.IsPhaseEndState(phaseName, oldState) {
   431→		return nil
   432→	}
   433→
   434→	// Check if this transition explicitly marks the phase as failed
   435→	if transitionConfig != nil && transitionConfig.failedPhase == phaseName {
   436→		// Mark as failed instead of completed
   437→		if err := state.MarkPhaseFailed(proj, phaseName); err != nil {
   438→			return &ErrPhaseStatusUpdate{Phase: phaseName, Operation: "mark failed", Cause: err}
   439→		}
   440→		return nil
   441→	}
   442→
   443→	// Normal case: mark as completed
   444→	if err := state.MarkPhaseCompleted(proj, phaseName); err != nil {
   445→		return &ErrPhaseStatusUpdate{Phase: phaseName, Operation: "mark completed", Cause: err}
   446→	}
   447→	return nil
   448→}
   449→
   450→// updateEnteringPhaseStatus updates the status of a phase when entering its start state.
   451→// Marks the phase as "in_progress" if currently "pending".
   452→func (ptc *ProjectTypeConfig) updateEnteringPhaseStatus(
   453→	newState State,
   454→	proj *state.Project,
   455→) error {
   456→	phaseName := ptc.GetPhaseForState(newState)
   457→	if phaseName == "" {
   458→		return nil
   459→	}
   460→
   461→	if !ptc.IsPhaseStartState(phaseName, newState) {
   462→		return nil
   463→	}
   464→
   465→	// MarkPhaseInProgress only updates if status is "pending"
   466→	if err := state.MarkPhaseInProgress(proj, phaseName); err != nil {
   467→		return &ErrPhaseStatusUpdate{Phase: phaseName, Operation: "mark in_progress", Cause: err}
   468→	}
   469→	return nil
   470→}
   471→

