// TestMemoryBackend_ConcurrentAccess tests thread safety.
func TestMemoryBackend_ConcurrentAccess(t *testing.T) {
	t.Run("concurrent reads do not block each other", func(t *testing.T) {
		backend := NewMemoryBackendWithState(&project.ProjectState{
			Name: "concurrent-test",
		})
		ctx := context.Background()

		var wg sync.WaitGroup
		errChan := make(chan error, 10)

		// Spawn multiple concurrent readers
		for i := 0; i < 10; i++ {
			wg.Add(1)
			go func() {
				defer wg.Done()
				_, err := backend.Load(ctx)
				if err != nil {
					errChan <- err
				}
			}()
		}

		wg.Wait()
		close(errChan)

		// Check for errors
		for err := range errChan {
			t.Errorf("unexpected error during concurrent read: %v", err)
		}
	})

	t.Run("concurrent Save and Load operations complete without data races", func(t *testing.T) {
		backend := NewMemoryBackend()
		ctx := context.Background()

		var wg sync.WaitGroup

		// Concurrent writers
		for i := 0; i < 10; i++ {
			wg.Add(1)
			go func(n int) {
				defer wg.Done()
				state := &project.ProjectState{
					Name: fmt.Sprintf("project-%d", n),
				}
				backend.Save(ctx, state)
			}(i)
		}

		// Concurrent readers
		for i := 0; i < 10; i++ {
			wg.Add(1)
			go func() {
				defer wg.Done()
				backend.Load(ctx)
				backend.Exists(ctx)
			}()
		}

		wg.Wait()
		// If we get here without race detector errors, test passes
	})

	t.Run("concurrent Exists operations are safe", func(t *testing.T) {
		backend := NewMemoryBackendWithState(&project.ProjectState{Name: "test"})
		ctx := context.Background()

		var wg sync.WaitGroup

		for i := 0; i < 20; i++ {
			wg.Add(1)
			go func() {
				defer wg.Done()
				backend.Exists(ctx)
			}()
		}

		wg.Wait()
	})

	t.Run("concurrent Delete operations are safe", func(t *testing.T) {
		backend := NewMemoryBackendWithState(&project.ProjectState{Name: "test"})
		ctx := context.Background()

		var wg sync.WaitGroup

		for i := 0; i < 10; i++ {
			wg.Add(1)
			go func() {
				defer wg.Done()
				backend.Delete(ctx)
			}()
		}

		wg.Wait()
	})
}

// TestMemoryBackend_DeepCopy tests that nested structures are properly copied.
func TestMemoryBackend_DeepCopy(t *testing.T) {
	t.Run("phases map is deep copied", func(t *testing.T) {
		original := &project.ProjectState{
			Name: "test",
			Phases: map[string]project.PhaseState{
